let messageFail = "One or more failure frequencies have been detected.";
let topicFail = "Failure detected";

let messageSuspect = "One or more frequencies are reaching high values.";
let topicSuspect = "Potential failure detected";

let failThreshold = -100; // -45
let suspectThreshold = -110; // -55

let i, fail = 0, suspect = 0;

for(i = 0; i < freqObject.length; i++)
{
    if(freqObject[i].value > failThreshold) fail++;
    if(freqObject[i].value > suspectThreshold) suspect++;
}

if(fail > 1 || suspect > 1)
{
    if(fail < suspect) {
        msg.payload = messageFail;
        msg.topic = topicFail;
        msg.ledStatus = "ADVISE";
    }
    else {
        msg.payload = messageSuspect;
        msg.topic = topicSuspect;
        msg.ledStatus = "FAIL";
    }
    msg.show = true;
}

return msg;

